package com.demo;

public class ContinueBreak {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				int i;
				for(i=1;i<=4;i++) 
				{ 
					if (i == 2)
						
	                {
						break;
						
						}		
					System.out.println(" " + i);

					continue;
				}
				}
	}
	
